import React, { useState } from 'react';
import quizData from './quizData';

function Quiz({ onQuizEnd }) {
  const [current, setCurrent] = useState(0);
  const [selected, setSelected] = useState([]);
  const [showError, setShowError] = useState(false);

  const handleOptionClick = (idx) => {
    const newSelected = [...selected];
    newSelected[current] = idx;
    setSelected(newSelected);
    setShowError(false);
  };

  const handleNext = () => {
    if (selected[current] === undefined) {
      setShowError(true);
      return;
    }
    if (current < quizData.length - 1) {
      setCurrent(current + 1);
    } else {
      onQuizEnd(selected);
    }
  };

  return (
    <div className="quiz-container">
      <h2>Question {current + 1} of {quizData.length}</h2>
      <p>{quizData[current].question}</p>
      <ul>
        {quizData[current].options.map((opt, idx) => (
          <li key={idx}>
            <button
              onClick={() => handleOptionClick(idx)}
              style={{
                background: selected[current] === idx ? '#90ee90' : '#fff',
                border: '1px solid #ccc',
                margin: '4px',
                padding: '8px 16px',
                cursor: 'pointer'
              }}
            >
              {opt}
            </button>
          </li>
        ))}
      </ul>
      {showError && <div style={{color: 'red'}}>Please select an option!</div>}
      <button onClick={handleNext} style={{marginTop: '16px'}}>
        {current === quizData.length - 1 ? 'Finish' : 'Next'}
      </button>
    </div>
  );
}

export default Quiz;
