import React from 'react';
import quizData from './quizData';

function Result({ selected, onRestart }) {
  const score = selected.reduce((acc, ans, idx) =>
    ans === quizData[idx].answer ? acc + 1 : acc, 0
  );

  return (
    <div className="result-container">
      <h2>Quiz Results</h2>
      <p>Your Score: {score} / {quizData.length}</p>
      <ul>
        {quizData.map((q, idx) => (
          <li key={idx}>
            <strong>{q.question}</strong><br/>
            Your answer: {q.options[selected[idx]] || 'No answer'}<br/>
            Correct answer: {q.options[q.answer]}
          </li>
        ))}
      </ul>
      <button onClick={onRestart} style={{marginTop: '16px'}}>Restart Quiz</button>
    </div>
  );
}

export default Result;
